import React, { useEffect, useContext } from "react";
import FlexContainer from "../components/FlexContainer";
import Header from "../components/Header";
import { Context as DataContext } from "../context/DataContext";

const Landing = () => {
  const { state, getData, extendResults, sortHomesByPrice } = useContext(DataContext);
  const { data, totalLength, priceSortValue } = state;

  useEffect(() => {
    getData();
  }, []);

  const fetchMoreHomes = (e) => {
    const currentAmount = data.length;
    extendResults({ currentAmount });
  };

  const sortByPrice = (e) => {
    const value = e.target.value;
    sortHomesByPrice({ priceSortValue: value })
  };

  return (
    <FlexContainer>
      <Header />
      <div className="hidden md:flex p-10 mt-24 w-screen justify-between bg-white">
        <div className="text-2xl text-gray-500">
          {totalLength} homes available
        </div>
        <div className="flex space-x-5 items-center">
          <div className="uppercase text-gray-500 text-bold whitespace-nowrap">sort by</div>
          <select
            className="focus:border-light-lightBlue-500 focus:ring-1 focus:ring-lightlightBlue-500 focus:outline-none w-full text-sm text-black placeholder-gray-500 border-gray-300 pl-2 pr-8"
            aria-label="form input"
            value={priceSortValue}
            name={priceSortValue}
            onChange={(e) => sortByPrice(e)}
          >
            <option value="high">Price: High to Low</option>
            <option value="low">Price: Low to High</option>
          </select>
        </div>
      </div>
      <div className="mt-24 md:mt-0 p-10 md:px-10 md:py-5 grid grid-cols-1 md:grid-cols-3 gap-5 max-w-6xl justify-self-center">
        {data.map((item) => (
          <div
            key={item.homeName}
            className="hover:shadow-sm transition transition-duration-200 shadow-md w-full flex flex-col"
          >
            <div
              className="w-full h-44 bg-center bg-no-repeat bg-cover"
              style={{ backgroundImage: `URL(${item.imageURL})` }}
            ></div>
            <div className="flex flex-col p-5 space-y-2">
              <h1 className="text-2xl">{item.homeName}</h1>
              <p className="inline text-xs text-gray-400">
                Starting in the ${item.startingPrice}s
              </p>
              <div className="inline text-xs">
                {item.sqft} sq. ft {item.beds} beds {item.baths} baths{" "}
                {item.isMultiSection ? "Multi Section" : null}
              </div>
            </div>
          </div>
        ))}
      </div>
      <button
        className="mb-24 w-11/12 md:w-1/3 max-w-xs h-16 rounded-lg bg-blue-500 text-lg text-white place-self-center"
        onClick={(e) => fetchMoreHomes(e)}
      >
        Load More
      </button>
      <div className="w-screen flex flex-wrap justify-start px-10 py-5 text-white bg-gray-500">
        <p>Copyright 2020 Clayton Homes. All Rights Reserved.</p>
      </div>
    </FlexContainer>
  );
};

export default Landing;
