import createDataContext from "./createDataContext";
import listings from "../data/listings"

const dataReducer = (state, action) => {
  switch (action.type) {
    case "GET_DATA":
      return {...state, data: action.payload.data, totalLength: action.payload.totalLength}
    case "EXTEND_LISTINGS":
      return {...state, data: action.payload}
    default:
      return state;
  }
};

const getData = (dispatch) => () => {
  const data = listings.slice(0,9)
  dispatch({ type: "GET_DATA", payload: {data: data, totalLength: listings.length} })
}

const extendResults = (dispatch) => ({ currentAmount }) => {
  const extendedListings = listings.slice(0, currentAmount += 3)
  dispatch({ type: "EXTEND_LISTINGS", payload: extendedListings })
}

export const { Provider, Context } = createDataContext(
  dataReducer,
  {getData, extendResults},
  { data: [], totalLength: 200 }
);
