import React, { useState, useEffect, useContext } from "react";
import { Context as DataContext } from "../context/DataContext";

const Landing = () => {
  const { state, getData, extendResults } = useContext(DataContext);
  const { data, totalLength } = state;

  useEffect(() => {
    getData();
  }, []);

  const fetchMoreHomes = (e) => {
      console.log("Inside fetchMoreHomes: ", data.length)
      const currentAmount = data.length
      extendResults({ currentAmount })
  }

  return (
    <div className="flex flex-col items-center justify-between font-sans">
      <div className="p-10 h-24 bg-white w-screen shadow-lg flex justify-center md:justify-start items-center fixed">
          <div className="w-44 h-16 bg-center bg-no-repeat bg-cover" style={{ backgroundImage: `URL(https://s3.amazonaws.com/media.widencdn.net/logos/claytonhomes/2daceb96-4bcb-4652-9e6c-7d527566cfa2/client-logo/Clayton_Corporate_Logo%401x.png)` }}></div>
      </div>
        <div className="hidden md:flex p-10 mt-24 w-screen justify-between fixed">
            <div className="text-2xl text-gray-500">{totalLength} homes available</div>
            <div>sort by</div>
        </div>
      <div className="mt-24 md:mt-24 p-10 md:px-10 md:py-5 grid grid-cols-1 md:grid-cols-3 gap-5 max-w-6xl justify-self-center">
        {data.map((item) => (
          <div key={item.homeName} className="hover:shadow-sm transition transition-duration-200 shadow-md w-full flex flex-col">
            <div className="w-full h-44 bg-center bg-no-repeat bg-cover" style={{ backgroundImage: `URL(${item.imageURL})` }}></div>
            <div className="flex flex-col p-5 space-y-2">
              <h1 className="text-2xl">{item.homeName}</h1>
              <p className="text-xs text-gray-400">
                Starting in the ${item.startingPrice}s
              </p>
              <div className="text-xs">
                {item.sqft} sq. ft {item.beds} beds {item.baths} baths{" "}
                {item.isMultiSection ? "Multi Section" : null}
              </div>
            </div>
          </div>
        ))}
      </div>
      <button className="mb-24 w-11/12 md:w-1/3 max-w-xs h-16 rounded-lg bg-blue-500 text-lg text-white place-self-center" onClick={(e) => fetchMoreHomes(e)}>Load More</button>
      <div className="w-screen flex flex-wrap justify-start px-10 py-5 text-white bg-gray-500">
        <p>Copyright 2020 Clayton Homes. All Rights Reserved.</p>
      </div>
    </div>
  );
};

export default Landing;
